// Khi document đã sẵn sàng, xóa các select đã được tùy chỉnh bởi thư viện nice-select
$(document).ready(function () {
    $(".nice-select").remove(); // Xóa các select đã được tùy chỉnh để tránh xung đột giao diện
});

// Khi document đã sẵn sàng, xử lý cập nhật động danh sách địa chỉ
$(document).ready(function () {
    // Sự kiện thay đổi tỉnh/thành phố trong form
    $("#province").change(function () {
        $("#district").removeClass("cursor-disable"); // Bỏ class vô hiệu hóa giao diện
        $("#district").removeAttr("disabled"); // Bật select quận/huyện

        // Gửi request AJAX để lấy danh sách quận/huyện
        $.get("/Account/GetDistrictsList", { province_id: $("#province").val() }, function (e) {
            $("#district").html("<option value>Quận/ Huyện</option>"); // Reset và thêm tùy chọn mặc định
            // Thêm từng quận/huyện vào select
            $.each(e, function (e, t) {
                $("#district").append("<option value='" + t.district_id + "'>" + t.type + " " + t.district_name + "</option>");
            });
        });
    });

    // Sự kiện thay đổi quận/huyện trong form
    $("#district").change(function () {
        $("#ward").removeClass("cursor-disable"); // Bỏ class vô hiệu hóa giao diện
        $("#ward").removeAttr("disabled"); // Bật select phường/xã

        // Gửi request AJAX để lấy danh sách phường/xã
        $.get("/Account/GetWardsList", { district_id: $("#district").val() }, function (e) {
            $("#ward").html("<option value>Phường/ Xã</option>"); // Reset và thêm tùy chọn mặc định
            // Thêm từng phường/xã vào select
            $.each(e, function (e, t) {
                $("#ward").append("<option value='" + t.ward_id + "'>" + t.type + " " + t.ward_name + "</option>");
            });
        });
    });
});

// Khai báo biến cho modal thêm địa chỉ trong quá trình đặt hàng
var createmodaladd = $("#ModalOrderAddCreate");

// Mở modal thêm địa chỉ khi click nút "Thêm địa chỉ mới"
$("#popupcreateaddress").click(function () {
    createmodaladd.modal("show"); // Hiển thị modal thêm địa chỉ
});

// Đóng modal thêm địa chỉ
$("#closemodal3").click(function () {
    createmodaladd.modal("hide"); // Đóng modal
});

// Hàm lưu địa chỉ mới trong quá trình đặt hàng
var SaveAddressOrder = function () {
    // Lấy dữ liệu từ form thêm địa chỉ
    var e = $("#address_name").val(); // Họ tên người nhận
    var t = $("#address_phone").val(); // Số điện thoại
    var r = $("#province").val(); // ID tỉnh/thành phố
    var d = $("#province :selected").text(); // Tên tỉnh/thành phố
    var n = $("#district").val(); // ID quận/huyện
    var i = $("#district :selected").text(); // Tên quận/huyện
    var a = $("#address_content").val(); // Địa chỉ cụ thể
    var s = $("#ward").val(); // ID phường/xã
    var o = $("#ward :selected").text(); // Tên phường/xã

    // Kiểm tra các trường bắt buộc
    if ("" == e || "" == t || "" == r || "" == n || "" == s || "" == a) {
        Swal.mixin({
            toast: true,
            position: "top-end",
            showConfirmButton: false,
            timer: 2500,
            didOpen: e => {
                e.addEventListener("mouseenter", Swal.stopTimer);
                e.addEventListener("mouseleave", Swal.resumeTimer);
            }
        }).fire({
            icon: "warning",
            title: "Nhập thông tin còn thiếu"
        });
        return false;
    }

    // Kiểm tra số điện thoại (phải đúng 10 chữ số)
    if (t.length < 10 || t.length > 10) {
        Swal.mixin({
            toast: true,
            position: "top-end",
            showConfirmButton: false,
            timer: 2500,
            didOpen: e => {
                e.addEventListener("mouseenter", Swal.stopTimer);
                e.addEventListener("mouseleave", Swal.resumeTimer);
            }
        }).fire({
            icon: "warning",
            title: "Số điện thoại phải đúng 10 chữ số"
        });
        return false;
    }

    // Kiểm tra độ dài họ tên (tối đa 20 ký tự)
    if (e.length > 20) {
        Swal.mixin({
            toast: true,
            position: "top-end",
            showConfirmButton: false,
            timer: 2500,
            didOpen: e => {
                e.addEventListener("mouseenter", Swal.stopTimer);
                e.addEventListener("mouseleave", Swal.resumeTimer);
            }
        }).fire({
            icon: "warning",
            title: "Họ tên không quá 20 ký tự"
        });
        return false;
    }

    // Kiểm tra độ dài địa chỉ cụ thể (tối đa 50 ký tự)
    if (a.length > 50) {
        Swal.mixin({
            toast: true,
            position: "top-end",
            showConfirmButton: false,
            timer: 2500,
            didOpen: e => {
                e.addEventListener("mouseenter", Swal.stopTimer);
                e.addEventListener("mouseleave", Swal.resumeTimer);
            }
        }).fire({
            icon: "warning",
            title: "Địa chỉ cụ thể không quá 50 ký tự"
        });
        return false;
    }

    // Gửi dữ liệu form qua AJAX để lưu địa chỉ
    var c = $("#create_address__order").serialize();
    $.ajax({
        type: "POST",
        url: "/Account/AddressCreate", // Gửi đến action AddressCreate
        data: c,
        success: function (r) {
            createmodaladd.modal("hide"); // Đóng modal
            if (1 == r) { // Nếu lưu thành công
                // Cập nhật thông tin địa chỉ vào form đặt hàng
                $(".order_address_name").attr("value", e); // Cập nhật họ tên
                $(".order_address_phone").attr("value", t); // Cập nhật số điện thoại
                $(".ck_address_province").attr("value", d); // Cập nhật tên tỉnh/thành phố
                $(".ck_address_district").attr("value", i); // Cập nhật tên quận/huyện
                $(".ck_address_ward").attr("value", o); // Cập nhật tên phường/xã
                $(".ck_address_content").attr("value", a); // Cập nhật địa chỉ cụ thể

                // Kích hoạt nút "Đặt hàng" và cập nhật giao diện
                $(".btn_submit_order").attr("id", "submit_order");
                $(".btn_submit_order").addClass("order_now");
                $(".btn_submit_order").attr("type", "submit");
                $(".message_order").text("(Đặt hàng để xác nhận thanh toán)");
                $(".btn_submit_order").removeAttr("disabled");
                $(".create_order_address").removeAttr("hidden");
                $(".order_now").removeClass("btn_submit_order");
                $(".ck_notexist_address").remove(); // Xóa thông báo "Chưa có địa chỉ"
                $(".order_address").text(a + ", " + o + ", " + i + ", " + d); // Cập nhật địa chỉ đầy đủ

                // Hiển thị thông báo thành công
                Swal.mixin({
                    toast: true,
                    position: "top",
                    showConfirmButton: false,
                    timer: 2000,
                    didOpen: e => {
                        e.addEventListener("mouseenter", Swal.stopTimer);
                        e.addEventListener("mouseleave", Swal.resumeTimer);
                    }
                }).fire({
                    icon: "success",
                    title: "Thêm địa chỉ thành công"
                });
            } else { // Nếu lưu thất bại
                Swal.mixin({
                    toast: true,
                    position: "top",
                    showConfirmButton: false,
                    timer: 1000,
                    didOpen: e => {
                        e.addEventListener("mouseenter", Swal.stopTimer);
                        e.addEventListener("mouseleave", Swal.resumeTimer);
                    }
                }).fire({
                    icon: "error",
                    title: "Lỗi"
                });
            }
        }
    });
};

// Khai báo biến cho modal thay đổi địa chỉ giao hàng
