// Hàm thay đổi địa chỉ giao hàng
var Change_order_address_confirm = function () {
    // Gửi dữ liệu form qua AJAX để thay đổi địa chỉ giao hàng
    var e = $("#change_order__address").serialize();
    $.ajax({
        type: "POST",
        url: "/Cart/ChangeAdressOrder", // Gửi đến action ChangeAdressOrder
        data: e,
        success: function (e) {
            change_od_add.modal("hide"); // Đóng modal
            if (1 == e) { // Nếu thay đổi thành công
                var t, r;
                // Lấy thông tin địa chỉ được chọn
                $('input[name="account_address_id"]:radio:checked').each(function () {
                    t = $(this).parent().find(".get_acc_username").text(); // Họ tên
                    r = $(this).parent().find(".get_acc_phone_num").text(); // Số điện thoại
                    _address = $(this).parent().find(".get_address").text(); // Địa chỉ đầy đủ
                    _address_province = $(this).parent().find(".get_address_province").text(); // Tên tỉnh/thành phố
                    _address_district = $(this).parent().find(".get_address_district").text(); // Tên quận/huyện
                    _address_ward = $(this).parent().find(".get_address_ward").text(); // Tên phường/xã
                    _address_content = $(this).parent().find(".get_address_content").text(); // Địa chỉ cụ thể
                });

                // Cập nhật thông tin địa chỉ trên giao diện
                $(".ck_address").text(_address); // Hiển thị địa chỉ đầy đủ
                $(".ck_username").val(t); // Cập nhật họ tên
                $(".ck_phone_num").val(r); // Cập nhật số điện thoại
                $(".ck_address_province").val(_address_province); // Cập nhật tên tỉnh/thành phố
                $(".ck_address_district").val(_address_district); // Cập nhật tên quận/huyện
                $(".ck_address_ward").val(_address_ward); // Cập nhật tên phường/xã
                $(".ck_address_content").val(_address_content); // Cập nhật địa chỉ cụ thể

                // Hiển thị thông báo thành công
                Swal.mixin({
                    toast: true,
                    position: "top",
                    showConfirmButton: false,
                    timer: 2000,
                    didOpen: e => {
                        e.addEventListener("mouseenter", Swal.stopTimer);
                        e.addEventListener("mouseleave", Swal.resumeTimer);
                    }
                }).fire({
                    icon: "success",
                    title: "Cập nhật địa chỉ thành công"
                });
            } else { // Nếu thay đổi thất bại
                Swal.mixin({
                    toast: true,
                    position: "top",
                    showConfirmButton: false,
                    timer: 1000,
                    didOpen: e => {
                        e.addEventListener("mouseenter", Swal.stopTimer);
                        e.addEventListener("mouseleave", Swal.resumeTimer);
                    }
                }).fire({
                    icon: "error",
                    title: "Lỗi"
                });
            }
        }
    });
};